import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './FormPage.css';

function FormPage() {
  const [capital, setCapital] = useState('');
  const [riskTolerance, setRiskTolerance] = useState('low');
  const [timeHorizon, setTimeHorizon] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:5000/submit_allocation', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ capital, risk_tolerance: riskTolerance, time_horizon: timeHorizon }),
    });

    if (response.ok) {
      const data = await response.json();
      navigate('/results', { state: { data } });
    } else {
      // Handle error
      console.error('Error submitting form');
    }
  };

  return (
    <div className="form-container">
      <h1>Investment Form</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="capital">Capital:</label>
          <input
            type="number"
            id="capital"
            value={capital}
            onChange={(e) => setCapital(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="risk-tolerance">Risk Tolerance:</label>
          <select
            id="risk-tolerance"
            value={riskTolerance}
            onChange={(e) => setRiskTolerance(e.target.value)}
            required
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="time-horizon">Time Horizon:</label>
          <input
            type="number"
            id="time-horizon"
            value={timeHorizon}
            onChange={(e) => setTimeHorizon(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FormPage;
