import React, { useState, useContext } from 'react';
import { Typography, Row, Col, Card, Button } from 'antd';
import { Link } from 'react-router-dom';
import { PlusOutlined } from '@ant-design/icons';
import { PortfolioContext } from './PortfolioContext';
import './Homepage.css';

const { Title } = Typography;
const mockMarketData = [
    // Commodities
    { commodity: 'Gold', price: '$1,800', change: '+0.5%' },
    { commodity: 'Silver', price: '$25', change: '+1.2%' },
    { commodity: 'Crude Oil', price: '$75', change: '-0.3%' },
    { commodity: 'Copper', price: '$4.10', change: '+0.8%' },
    
    // Stocks (Nifty)
    { commodity: 'Nifty 50', price: '18,000', change: '+0.9%' },
    { commodity: 'Nifty Bank', price: '41,500', change: '-0.2%' },

    // Currency Pairs
    { commodity: 'USD/INR', price: '75.50', change: '-0.1%' },
    { commodity: 'EUR/USD', price: '1.12', change: '+0.4%' },
    
    // Cryptocurrencies
    { commodity: 'Bitcoin', price: '$35,000', change: '+2.5%' },
    { commodity: 'Ethereum', price: '$2,500', change: '+3.1%' },
];


const sections = {
    'A': {
        'safe': ['Reliance Industries Limited (RELIANCE) - Stock', 'Tata Consultancy Services (TCS) - Stock'],
        'hedge': ['Adani Enterprises Limited (ADANIENT) - Stock', 'Bharti Airtel Limited (BHARTIARTL) - Stock'],
        'volatile': ['Adani Enterprises Limited (ADANIENT) - Stock', 'Bharti Airtel Limited (BHARTIARTL) - Stock']
    },
    'B': {
        'safe': ['Gold', 'Silver'],
        'hedge': ['Aluminum', 'Zinc'],
        'volatile': ['Cocoa', 'Cotton']
    },
    'C': {
        'safe': ['Gold', 'Silver'],
        'hedge': ['Aluminum', 'Zinc'],
        'volatile': ['Cocoa', 'Cotton']
    }
};

const getRandomAssets = (assets, count) => {
    const shuffled = [...assets].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
};

const Homepage = () => {
    const { addAssetToPortfolio } = useContext(PortfolioContext); // Use the context

    return (
        <>
            <Title level={2} className="heading">Market Data</Title>
            <Row gutter={[32, 32]}>
                {mockMarketData.map((data, index) => (
                    <Col span={8} key={index}>
                        <Card title={data.commodity} bordered={false}>
                            <p>Price: {data.price}</p>
                            <p>Change: {data.change}</p>
                        </Card>
                    </Col>
                ))}
            </Row>

            <Title level={2} className="heading">Highlighted Assets</Title>
            <Row gutter={[32, 32]}>
                {Object.keys(sections).map((sectionKey) => (
                    <Col span={8} key={sectionKey}>
                        <Card title={`Section ${sectionKey}`} bordered={false}>
                            {Object.keys(sections[sectionKey]).map((category) => (
                                getRandomAssets(sections[sectionKey][category], 2).map((asset, index) => (
                                    <div key={index}>
                                        <Link to={`/asset/${asset.split(' ')[0].toLowerCase()}`}>{asset}</Link>
                                        <Button
                                            icon={<PlusOutlined />}
                                            size="small"
                                            onClick={() => addAssetToPortfolio(asset)}
                                            style={{ marginLeft: '10px' }}
                                        />
                                    </div>
                                ))
                            ))}
                        </Card>
                    </Col>
                ))}
            </Row>

            <div className="home-heading-container">
                <Title level={2} className="home-title">More Details</Title>
                <Title level={3}><Link to="/sections">View All Sections</Link></Title>
                <Title level={3}><Link to="/form">Investment Allocation Form</Link></Title>
                <Title level={3}><Link to="/portfolio">View Portfolio</Link></Title>
                <Title level={3}><Link to="/news">Market News</Link></Title> {/* Link to News */}
            </div>
        </>
    );
};

export default Homepage;
